import React from "react";
import { useDispatch } from "react-redux";
import { setGetPostsPage, setEditPostsPage } from "../redux/action";
import "../scss/components/pagination.scss";

const Pagination = ({
  currentPage,
  postsPerPage,
  totalPosts,
  setCurrentPage,
}) => {
  const paginPageWithPosts = [];
  const [response, setResponse] = React.useState([])


  const dispatch = useDispatch();

  for (let i = 1; i <= Math.ceil(totalPosts / postsPerPage); i++) {
    paginPageWithPosts.push(i);
  }

   React.useEffect(() => {
    fetch(
      `https://ekreative-json-server.herokuapp.com/posts?_page=${currentPage}&_limit=${postsPerPage}`
    )
      .then((response) => response.json())
      .then((data) => { 
        setEditPostsPage(currentPage, postsPerPage)

       });
  }, [])


  const pagFunc = (number) => {
    setCurrentPage(number)


      // dispatch(setEditPostsPage(currentPage, postsPerPage));
  };



  return (
    <div className="pagination">
      {paginPageWithPosts.map((number, index) => (
        <button
          id={`${index + 1}`}
          key={number}
          onClick={() => {
            pagFunc(number)
           }}
          className={index + 1 === currentPage ? "active" : "button-pag"}
        >
          {number}
        </button>
      ))}
    </div>
  );
};

export default Pagination;
