import React from 'react';
import axios from 'axios';

export function getSingup(props) {
   let authData = {
      email: props.email,
      name: props.name,
      password: props.password,
      returnSecureToken: true
    }
  console.log('signup');
  if (authData.email != '') {
    axios.post('https://ekreative-json-server.herokuapp.com/register', authData)
      .then(response => this.setState({ authData: response.data.id }));
  }
}

