import {
  ADD_DATA_TO_EDIT,
  ANNOUN_USER,
  DATA_ANNOUN,
  DATA_COMMENT,
  DATA_OF_POST,
  DATA_USER,
  GET_POSTS_PAGE,
  POSTS,
  UPDATE_POST,
} from "./action";

const initialState = {
  posts: [],
  announcements: []
};


export const postReducer = (state = [], action) => {
  switch (action.type) {
    case ADD_DATA_TO_EDIT:
      const { id, title, body, userId, createdAt, key } = action;
      return [
        {
          id,
          title,
          body,
          userId,
          createdAt,
          key,
        },
      ];

    default:
      return state;
  }
};

export const userReducer = (state = [], action) => {
  switch (action.type) {
    case DATA_USER:
      const { id, email, firstname, lastname, age, avatar } = action;
      console.log(email);
      return [
        {
          id,
          email,
          firstname,
          lastname,
          age,
          avatar,
        },
      ];
    default:
      return state;
  }
};

export const paginateReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_POSTS_PAGE:
      return {
        ...state,
        posts: action.payload
      }
    default:
      return state;
  }
};

export const announUserReducer = (state = [], action) => {
  switch (action.type) {
    case ANNOUN_USER:
      return (state = action.dataUser);
    default:
      return state;
  }
};
export const announcementsReducer = (state = {}, action) => {
  switch (action.type) {
    case ANNOUN_USER:
      return {
        ...state,
      }
    case DATA_ANNOUN:
      return {
        ...state,
        announcements: action.payload
      }
    default:
      return state;
  }
};

export const dataPostReducer = (state = {}, action) => {
  switch (action.type) {
    case DATA_OF_POST:
      return {
        ...state,
        dataPost: action.payload
      };
    default:
      return state;
  }
};

export const posts = (state = initialState, action) => {
    switch (action.type) {
        case POSTS:
      return {
        ...state,
        posts: action.payload,
      };

    case UPDATE_POST:
      return {
        ...state,
        posts: state.posts.map((post) => {
          if (post.id === action.payload.id) {
            return action.payload;
          }
          return post;
        }),
      };
    default:
      return state;
  }
};
