// import classNames from 'classnames'
import React from 'react'
import { useDispatch } from 'react-redux'
import { setGetPostsPage } from '../redux/action'
import '../scss/components/pagination.scss'

const Pagination = ({currentPage, postsPerPage, totalPosts }) => {
    // const [active, setActive] = React.useState(false)
    const paginPageWithPosts = []


    const dispatch = useDispatch()

    for (let i = 1; i <= Math.ceil(totalPosts / postsPerPage); i++) {
        paginPageWithPosts.push(i)
    }



    const pagFunc = (number, index) => {
        dispatch(setGetPostsPage(number, postsPerPage))
        // paginate(number)
        // setActive(true)

        currentPage= number

         let active = document.getElementById(`${index + 1}`)

        if (active.id = currentPage) {
                active.className = 'active'
        } else {
            active.className=''
        }


    }

    return (
        <div className='pagination'>
            {
                paginPageWithPosts.map((number, index) => (
                    <button id={`${index + 1}`} key={number} onClick={() => pagFunc(number, index)} className={index + 1 === currentPage ? 'active' : 'button-pag' }>{number}</button>
                )
                )
            }
        </div>
    )
}

export default Pagination
