import axios from "axios";
import React from "react";
import { Button } from ".";
import "../scss/components/createPost.scss";

var currentdate = new Date();
var datetime = currentdate.getFullYear() + "-" + currentdate.getMonth() 
+ "-" + currentdate.getDay() + "T" 
+ currentdate.getHours() + ":" 
+ currentdate.getMinutes() + ":"
  + currentdate.getSeconds();


function CreatePost() {
  const [post, setPost] = React.useState({
    title: "",
    body: ""
  });

  console.log(post);
    let dataAboutUser = JSON.parse(localStorage.user);

  const getCreatePost = () => {
      const data = {
          title: post.title,
          body: post.body,
          userId: dataAboutUser.id,
          createAt: datetime,
          updatedAt: datetime
      }

      const headers = {'Authorization': `Bearer ${localStorage.getItem('token').slice(1, -1)}`}

      axios.post('https://ekreative-json-server.herokuapp.com/664/posts', data, {headers})
  };

  function handleChange(e) {
      setPost({ [e.target.title]: e.target, [e.target.body]: e.target});
    }

  return (
    <div className="create-post">
      <h2>create your post</h2>
      <form action="">
        <div className="title-block">
          <input
            type="text"
            className="input-for-title"
            value={post.title}
            // name='title'
            placeholder='title of your post'
            onChange={handleChange}
          />
          <textarea
            type="text"
            className="input-for-post"
            value={post.body}
            // name='body'
            placeholder='write here your story or dream or somthing else...'
            onChange={handleChange}
          ></textarea>
        </div>

        <Button type="button" className="button-create" onClick={getCreatePost}>
          Create
        </Button>
      </form>
    </div>
  );
}

export default CreatePost;
