import axios from "axios";
import React from "react";
import "../scss/components/postBlock.scss";
import "../scss/components/getPosts.scss";

// import { Route, Link, NavLink } from "react-router-dom";
import classNames from "classnames";
import { setAddData } from "../redux/action";
import { useDispatch } from "react-redux";

var currentdate = new Date();
var datetime = currentdate.getFullYear() + "-" + currentdate.getMonth() 
+ "-" + currentdate.getDay() + "T" 
+ currentdate.getHours() + ":" 
+ currentdate.getMinutes() + ":"
+ currentdate.getSeconds();

const PostBlock = ({ id, title, body, userId, createdAt }) => {
  const [usersData, setUsersData] = React.useState([]);

  let data = JSON.parse(localStorage.user);


  const dispatch = useDispatch();

  const handleChange = (e) => {
    setUpdateCommentBody((prevState) => ({prevState, value: e.target.value}))
  }

  // console.log('user data', dataUserComment);
  React.useEffect(() => {
    fetch(`https://ekreative-json-server.herokuapp.com/users/${userId}`)
      .then((response) => response.json())
      .then((data) => {
        setUsersData(data);
      });
  }, []);

  React.useEffect(() => {
    fetch(
      `https://ekreative-json-server.herokuapp.com/comments?postId=${id}&_sort=createdAt&_order=asc`
    )
      .then((response) => response.json())
      .then((data) => {
        setComments(data);
        data.map((comment) =>
          setDataComment({
            id: comment.id,
            postId: comment.postId,
            body: comment.body,
            userId: comment.userId,
            createdAt: comment.createdAt,
            updatedAt: comment.updatedAt,
          })
        );
      });
  }, []);

  // console.log('id', dataComment.userId);

  // React.useEffect(() => {
  //   fetch(
  //     `https://ekreative-json-server.herokuapp.com/users/${dataComment.userId}`
  //   )
  //     .then((response) => response.json())
  //     .then((data) => {
  //       setDataUserComment(data);
  //       // console.log(data);
  //     });
  // }, []);

  function deletePost() {
    axios.delete(`https://ekreative-json-server.herokuapp.com/664/post/${id}`);
  }

  const editPost = (id, title, body, userId, createdAt) => {
    dispatch(setAddData(id, title, body, userId, createdAt));
  };

  const addComment = () => {
    const data = {
      userId: data.userId,
      body: '',
      createdAt: datetime,
      updatedAt: datetime,
    }
    axios.post(`https://ekreative-json-server.herokuapp.com/664/comments`, data);
  };
  const updateComment = ({id, postId, body, userId, createdAt}, updateBody) => {
    const data = {
      id, 
      postId,
      body: updateBody,
      userId,
      createdAt,
      updatedAt: datetime 
    }
    axios.patch(
      `https://ekreative-json-server.herokuapp.com/664/comments/${id}`,data
    );

     setUpdateCommentBody(prevState => ({
      prevState,
      openUptComm: false
    }))
  };

  const openUpdateBlock = (id) => {
    setUpdateCommentBody(prevState => ({
      prevState,
      openUptComm: true
    }))
  }


  return (
    <div className="post-block">
      <div className="content-post">
        <div className="content">
          <h3>{title}</h3>
          <p>{body}</p>
          <p>
            {usersData.firstname} {usersData.lastname}
          </p>
        </div>
        {localStorage.getItem("token") && usersData.id === data.id ? (
          <div className="content-button">
            <button
              className={classNames("button-post", "button")}
              onClick={() => editPost(id, title, body, userId, createdAt)}
            >
              Edit
            </button>
            <button
              className={classNames("button-post", "button")}
              onClick={deletePost}
            >
              Delete
            </button>
          </div>
        ) : (
          ""
        )}
      </div>


    </div>
  );
};

export default PostBlock;
