import React from "react";
import { Button, Input } from ".";

function LoginForm({ Login, error }) {

  const [details, setDetails] = React.useState({
    email: {
      type: 'email',
      value: '',
      lable: 'email'
    },
    password: {
      type: 'password',
      value: '',
      lable: 'password'
    }
  })

  const submitHendler = e => {
    e.preventDefault();

    Login(details)
  }

  const renderInput = () => {
    return Object.keys(details)
  }

  return (
    <div>
      <div className="form">
        <form onSubmit={submitHendler}>
          <h2>Log In</h2>
          {renderInput()}
        </form>
      </div>
      <div className="block-of-buttons">
          <Button
            value="Log In"
            className="singUp"
          />
          <Button
            value="Sing Up"
            className="logIn"
          />
      </div>
    </div>
  );
}

export default LoginForm;
