import axios from 'axios';
import React from 'react';
import '../scss/components/postBlock.scss'


function PostBlock({ title, body, userId }) {

    const userOfPost = (userId) => {
        axios.get(`https://ekreative-json-server.herokuapp.com/users + {userId}`)
    }

    return (
        <div className='post-block'>
            <h3>{title}</h3>
            <p>{body}</p>

        </div>
    )
}

export default PostBlock;
