          required: true,
          userName: true,
        },
      },
      email: {
        value: "",
        type: "email",
        label: "email",
        errorMessage: "enter correct email",
        valide: false,
        touched: false,
        validation: {
          required: true,
          email: true,
        },
      },
      password: {
        value: "",
        type: "password",
        label: "password",
        errorMessage: "enter correct password",
        valide: false,
        touched: false,
        validation: {
          required: true,
          password: 9,
        },
      },
      confirmationPassword: {
        value: "",
        type: "password",
        label: "confirmation password",
        errorMessage: "enter correct password",
        valide: false,
        touched: false,
        validation: {
          required: true,
          confirmationPassword: true,
        },
      },
      age: {
        value: "",
        type: "number",
        label: "age",
        errorMessage: "fill in the field",
        valide: false,
        touched: false,
        validation: {
          required: true,
          age: true,
        },
      },
      avatar: {
        value: "",
        type: "url",
        label: "your avatar",
        errorMessage: "enter correct url",
        valide: false,
        touched: false,
      },
    },
  });


  return (
    <div className="authurization">
      <h1>Authurization</h1>

    </div>
  );
}

export default Authorization;
