import React from "react";
import "./../scss/app.scss";
import { Input, Button } from "../conponents/index";
import { Link } from "react-router-dom";
import { getSingup } from '../conponents/server'


function Singup() {
  const [user, setUser] = React.useState({
    name: '',
    email: '',
    password: ''
  });

  const changeHandler = (e) => {
    ([e.target.label]: e.target.value)
  }


  return (
    <div className="authorization">
      <h2>Sing Up</h2>
      <div className="form">
        <form>
          <Input
            type="text"
            value={user.name}
            onChange={changeHandler}
            label="full name"
          />
          <Input
            type="text"
            value={user.email}
            onChange={changeHandler}
            label="email"
          />
          <Input
            type="password"
            value={user.password}
            onChange={changeHandler}
            label="password"
          />
        </form>
      </div>
      <Button
        type="submit"
        onClick={getSingup(user)}
      >
        <Link to="/login">Login</Link>
        Singup
      </Button>
    </div>
  );
}

export default Singup;
