import axios from "axios";
import React from "react";
import { Link } from "react-router-dom";

function Login(props) {
  const [email, setEmail] = React.useState("");
  const [password, setPassword] = React.useState("");

  function getLogin() {
    const data = {
      email: email,
      password: password,
    };

    axios.post("https://ekreative-json-server.herokuapp.com/login", data)
      .then((response) => {
        localStorage.setItem('token', response.token)
      })
  }

  const handleSubmit =  e => {
    e.preventDefault();

  }

  return (
    <div className="authorization">
      <h2>Log In</h2>
      <div className="form">
        <form onSubmit={handleSubmit}>
          <div className='input'>
            <input type="email" placeholder="email" value={email} onChange={(e) => setEmail(e.target.value)} />
          </div>

          <div className='input'>
            <input type="password" placeholder="password" value={password} onChange={(e) => setPassword(e.target.value)} />
          </div>

          <button type='button' className='button' onClick={getLogin} >Login</button>
          <br />
          <p>if you dont have an account </p>
          <br />
         <Link to='/signup'><button type='button' className='button'  onClick={getLogin} >Signup</button></Link>
        </form>
      </div>
    </div>
  );
}

export default Login
