import { ADD_DATA_TO_EDIT } from "./action";


let nextTodoId = 0;
export const postReducer = (state=[], action) => {
    switch (action.type) {
        case ADD_DATA_TO_EDIT:
            const { id, title, body, userId, createdAt, key } = action
            return [
                {
                    id,
                    title,
                    body,
                    userId,
                    createdAt,
                    key
                }
            ]

        default:
            return state;
    }
}
