import React from 'react';
import { Input } from '../conponents';


function Login() {

    const [user, setUser] = React.useState({
        formData: {
            email: {
                type: "email",
                value: "",
                label: "email",
                touched: false,
            },
            password: {
                type: "password",
                value: "",
                label: "password",
                touched: false,
            },
        }
    })

    const onChangeHandler = (event, dataName) => {
        const formData = { ...user.formData };
        const data = { ...formData[dataName] };

        data.value = event.target.value;
        // data.touched = true;

        formData[dataName] = data;




        setUser((prevState) => ({
            ...prevState,
            formData,
        }));
    }

    const renderInputs = () => {
    return Object.keys(user.formData).map((dataName, index) => {
      const data = user.formData[dataName];
      return (
        <Input
          key={dataName + index}
          type={data.type}
          value={data.value}
          valid={data.valid}
          touched={data.touched}
          label={data.label}
          shouldValidate={!!data.validation}
          errorMessage={data.errorMessage}
          onChange={(event) => onChangeHandler(event, dataName)}
        />
      );
    });
  };


    return (
        <div className="authorization">
        <h2>Log In</h2>
      <div className="form">
        <form>{renderInputs()}</form>
      </div>
    </div>
    )
}

export default Login;
