import React from "react";
import { Button, Input } from ".";
import '../scss/app.scss'

function LoginForm({ Login, user, error }) {

  // const [details, setDetails] = React.useState({
  //   email: {
  //     type: 'email',
  //     value: '',
  //     label: 'email'
  //   },
  //   password: {
  //     type: 'password',
  //     value: '',
  //     label: 'password',
  //   }
  // })

  const submitHendler = e => {
    e.preventDefault();

    Login(user)
  }

  const renderInput = () => {
    return Object.keys(user).map((dataIput, index) => {
      const data = user[dataIput]

      return (
        <Input
          type={data.type}
          value={data.value}
          label={data.label}
        />
      )
    })
  }

  return (
    <div className='authorization'>
      <div className="form">
          <h2>Log In</h2>
        <form onSubmit={submitHendler}>
          {renderInput()}
          {/* <Input
            types={email.types, password.types}
            value={email.value, password.value}
            label={email.label, password.label}
          /> */}
        </form>
      </div>
      <div className="block-of-buttons">
          <Button
            value="Log In"
            className="singUp"
          />
          <Button
            value="Sing Up"
            className="logIn"
          />
      </div>
    </div>
  );
}

export default LoginForm;
