import React from "react";
import "./../scss/app.scss";

function Authorization() {
  const [user, setUser] = React.useState({
    formData: {
      fistname: {
        type: "name",
        value: "",
        label: "first name",
        errorMessage: "firstname cannot be empty",
        touched: false,
        valid: false,
      },
      lastname: {
        type: "name",
        value: "",
        label: "last name",
        errorMessage: "lastname cannot be empty",
        valid: false,
        touched: false,
      },
      email: {
        type: "email",
        value: "",
        label: "email",
        errorMessage: "put correct email",
        valid: false,
        touched: false,
      },
      password: {
        type: "password",
        value: "",
        label: "password",
        errorMessage: "password must have least 9 signs",
        valid: false,
        touched: false,
      },
      confirmPassword: {
        type: "password",
        value: "",
        label: "confirmation password",
        errorMessage: "put correct password",
        valid: false,
        touched: false,
      },
      age: {
        type: "age",
        value: "",
        label: "age",
        errorMessage: "age cannot be empty",
        valid: false,
        touched: false,
      },
      avatar: {
        type: "url",
        value: "",
        label: "avatar",
        errorMessage: "avatar cannot be empty",
        valid: false,
        touched: false,
      },
    }
  });

  const renderInputs = () => {
    return Object.keys(user.formData).map((dataName, index) => {
      const data = user.formData[dataName];
      return (
        <Input
          key={dataName + index}
          type={data.type}
          value={data.value}
          valid={data.valid}
          touched={data.touched}
          label={data.label}
          shouldValidate={!!data.validation}
          errorMessage={data.errorMessage}
          onChange={(event) => onChangeHandler(event, dataName)}
        />
      );
    });
  }

  return (
    <div className="authorization">
      <h1>Sing Up</h1>
        {renderInputs()}
    </div>
  );
}

export default Singup;
