import axios from "axios";
import React from "react";
import { Button, PostBlock } from ".";
import "../scss/components/getPosts.scss";

const GetPosts = () => {
  const [posts, setPosts] = React.useState([]);
  const [isLoaded, setIsLoaded] = React.useState(false);
  const [error, setError] = React.useState(null);

  React.useEffect(() => {
    fetch("https://ekreative-json-server.herokuapp.com/664/posts")
      .then((response) => response.json())
      .then((data) => {
        setPosts(data);
      });
  }, []);

  return (
    <div className="get-posts">
      {posts.map((dataPost) => (
        <PostBlock key={dataPost.id} {...dataPost} />
      ))}

      <div className="create-post">
        <h2>update your post</h2>
        <form action="">
          {/* <div className="title-block"> */}
            <p className="header-for-post-form">title</p>
            <input
              type="text"
              className="input-for-title"
              // value={post.title}
              // onChange={handleChange}
            />
          {/* </div> */}

          {/* <div className="post-block"> */}
            <p className="header-for-post-form">post</p>
            <textarea
              type="text"
              className="input-for-post"
              // value={post.body}
              // onChange={handleChange}
            ></textarea>
          {/* </div> */}

          <Button type="button" className="button-update">
            Edit
          </Button>
        </form>
      </div>
    </div>
  );
};

export default GetPosts;
